import axios from 'axios'

import { apiUrl as baseURL } from '../../config'

export default class Api {

    static Get(url, data) {
        return Api.Request(url, 'GET', data)
    }

    static Post(url, data) {
        return Api.Request(url, 'POST', data)
    }

    static Request(url, method, data) {
        
        return new Promise(async (resolve, reject) => {

            try {

                const { data } = await axios({ method, baseURL, url, data: data })

                resolve(data)
            }
            catch(error) {
                resolve({error})
            }
        });
    }

    static HeaderOptions() {

        return {
            headers: {
                'Content-Type': 'application/json',
                'Content-Length': 45
            }
          }

    }

}
