import axios from 'axios'

import { apiUrl } from '../../config'

export default class Api {

    static Get(url, data) {

    }

    static Post(url, data) {
        this.Request(this.Url(url), 'POST', data)
    }

    static Request(url, method, data) {
        
        return new Promise(async (resolve, reject) => {

            try {

                const data = await axios({ method, url, data: JSON.stringify(data) })

                debugger;
                
                resolve()

            }
            catch(error) {
                resolve({error});
            }
        });
    }

    static Url(url) { return `${apiUrl}/${url}` }

}
