import axios from 'axios'

import { apiUrl } from '../../config'

export default class Api {

    static Get(url, params) {
        return Api.Request(Api.Url(url), 'GET', params)
    }

    static Post(url, params) {
        return Api.Request(Api.Url(url), 'POST', params)
    }

    static Request(url, method, dparamsata) {
        
        return new Promise(async (resolve, reject) => {

            try {

                const { data } = await axios({ method, url, data: JSON.stringify(data) })

                resolve(data)
            }
            catch(error) {
                resolve({error})
            }
        });
    }

    static Url(url) {
        return `${apiUrl}/${url}`
    }

    static HeaderOptions() {

        return {
            headers: {
                'Content-Type': 'application/json',
            }
          }

    }

}
