import axios from 'axios'
import { call, put, takeEvery, all, select } from 'redux-saga/effects'

import { accessTokenSelector } from '../../ducks/auth'
import { apiUrl as baseURL } from '../../config'

export default class Api {

    static Get = function* (url, data, needAuthorization) {

        debugger
        const token = yield select(accessTokenSelector)
        debugger

        return yield Api.Request(url, 'GET', data, needAuthorization)
    }

    static Post = function* (url, data, needAuthorization) {
        return yield Api.Request(url, 'POST', data, needAuthorization)
    }

    static Request = function* (url, method, data, needAuthorization) {
        
        yield call(new Promise(async (resolve, reject) => {

            try {

                const { data: result } = await axios({ method, baseURL, url, data, headers: this.HeaderOptions() })

                resolve(result)
            }
            catch(error) {
                resolve({error})
            }
        }));
    }

    static HeaderOptions(needAuthorization) {

        return {
            headers: {
                
            }
          }

    }

}
