import React, { Component } from 'react'
import { connect } from 'react-redux'
import { Route } from 'react-router-dom'
import { isAuthorizeSelector } from '../../ducks/auth'

class ProtectedRoute extends Component {

    render() {
        const { isAuthorized, component, ...rest } = this.props
        return <Route {...rest} render={this.getComponent()} />
    }

    getComponent = () => {
        const { isAuthorized, ...rest } = this.props
        return isAuthorized? <Route {...rest} /> : <h1>Авторизуйтесь</h1>
    }

}

export default connect(
    (state) => ({
        isAuthorized: isAuthorizeSelector(state)
    }),
    null,
    null,
    { pure: false }
)(ProtectedRoute)
