import axios from 'axios'
import { accessTokenSelector } from '../../ducks/auth'

import { apiUrl as baseURL } from '../../config'
import { select } from 'redux-saga/effects'

export default class Api {

    static Get(url, data, needAuthorization) {

        select(accessTokenSelector)

        return Api.Request(url, 'GET', data, needAuthorization)
    }

    static Post(url, data) {
        return Api.Request(url, 'POST', data, needAuthorization)
    }

    static Request(url, method, data, needAuthorization) {
        
        return new Promise(async (resolve, reject) => {

            try {

                const { data: result } = await axios({ method, baseURL, url, data })

                resolve(result)
            }
            catch(error) {
                resolve({error})
            }
        });
    }

    static HeaderOptions(needAuthorization) {

